import React from 'react';
import logo from './logo.svg';
import './App.css';
// hash模式和history模式，history需要后端配合
import {BrowserRouter as Router, Link, NavLink} from 'react-router-dom'


function Home() {
  return (
    <div>
      <h1>首页</h1>
    </div>
  )
}

function Me() {
  return (
    <div>
      <h1>我的</h1>
    </div>
  )
}

// 函数式组件
function App() {
  return (
    <div id="app">
      <Router>
        <div className="nav">
          <Link to="/">Home</Link>
          <Link to="/me">个人中心</Link>
        </div>
        <NavLink path="/" exact component={Home}></NavLink>
        <NavLink path="/me" exact component={Me}></NavLink>
      </Router>
    </div>
  );
}

export default App;
