import React from 'react';
import ReactDOM from 'react-dom';
import './04study.css'


// 函数式组件
function Childcom() {
  let title = <h2>这是副标题</h2>
  let weather = '下雨'
  let isGo = weather === '下雨' ? '不出门' : '出门'
  return (
    <div>
      <h1>hellow world!</h1>
      {title}
      <div>
        天气
        <span>{weather}</span>
        是否出门:
        <span>{isGo}</span>
      </div>
    </div>
  )
}


// 类组件定义
class HellowWorld extends React.Component {
  render() {
    return (
      <div>
        <h1>类组件HellowWorld</h1>
      </div>
    )
  }
}



ReactDOM.render(
  <HellowWorld />,
  document.querySelector('#root')
)
