// react 函数式组件
function Clock(props) {
  return (
    <div>
      <h1>现在时间是：{props.data.toLocaleTimeString()}</h1>
      <h2>这是副标题 </h2>
    </div>
  )
}

function run() {
  ReactDOM.render(
    <Clock data={new Date()} />,
    document.querySelector('#root')
  )
}

setInterval(run, 1000)
