import React from 'react';
import ReactDOM from 'react-dom';

class Clock extends React.Component{
  constructor(props) {
    super(props)
    // 状态-> 视图
    this.state = {
      time:new Date().toLocaleString()
    }
  }

  render(){
    return(
      <div>
        <h1>当前时间{this.state.time}</h1>
      </div>
    )
  }
}



ReactDOM.render(
  document.querySelector('#root')
)
